import { Component, OnInit } from '@angular/core';
import { AddBlogPost } from '../models/add-blogpost.model';
import { Blogpost } from '../models/blog-post.model';
import { BlogPostService } from '../services/blog-post.service';
import { Router } from '@angular/router';
import { CategoryService } from '../../category/services/category.service';
import { Category } from '../../category/Models/categoriy.model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-add-blogpost',
  templateUrl: './add-blogpost.component.html',
  styleUrls: ['./add-blogpost.component.css']
})
export class AddBlogpostComponent implements OnInit {

  model:AddBlogPost;
  category$?: Observable<Category[]>;
  constructor(private blogPostService: BlogPostService,private router:Router, private categoryService:CategoryService){
    this.model={
      title:'',
      shortDescription:'',
      urlHandle:'',
      featuredImageUrl:'',
      content:'',
      publishedDate:new Date(),
      author:'',
      isVisible:true,
      categories:[],
    }
  }
  ngOnInit(): void {
    this.category$=this.categoryService.getAllCategories();
  }
  onSubmit():void{
    console.log(this.model);
    this.blogPostService.createBlogPost(this.model).subscribe({
      next:(response)=>{
        this.router.navigateByUrl('/admin/blogposts');
      }
    });

  }
}
function subscribe(arg0: { next: (response: any) => void; }) {
  throw new Error('Function not implemented.');
}
